#!/usr/bin/env python3

# Day 1 Part 2

captcha = ''

def solve(captcha):
    cap_len = len(captcha)
    result = 0
    for i in range(cap_len):
        j = int(i + cap_len / 2)
        if j >= cap_len:
            j = j - cap_len
        if captcha[i] == captcha[j]:
            result += int(captcha[i])
    return result

# print(solve('1212')) #6
# print(solve('1221')) #0
# print(solve('123425')) #4
# print(solve('123123')) #12
# print(solve('12131415')) #4
print(solve(captcha))
