def captcha(input):
    sum = 0
    length = len(input)
    for index in range(0, length): 
        nextIndex = getNextIndex(index, length)
        sum += getValueIfEqualElseZero(input, index, nextIndex)
    return sum

def getNextIndex(currentIndex, length):
    nextIndex = currentIndex + length / 2
    if(nextIndex >= length):
        nextIndex = nextIndex - length
    return nextIndex

def getValueIfEqualElseZero(string, index1, index2):
    n0 = int(string[index1])
    n1 = int(string[index2])
    if n0 == n1:
        return n0
    else:
        return 0

