# Part 2
def solve(input):
    n = len(input)
    matches_next = [
        int(input[i]) for i in range(n)
        if input[i] == input[(i + n/2) % n]
    ]
    return sum(matches_next)


assert solve('1212') == 6 #: the list contains 4 items, and all four digits match the digit 2 items ahead.
assert solve('1221') == 0 # because every comparison is between a 1 and a 2.
assert solve('123425') == 4 # because both 2s match each other, but no other digit has a match.
assert solve('123123') == 12
assert solve('12131415') == 4

print(solve(''))
