package day1

import (
	"github.com/stretchr/testify/assert"
	"testing"
)

func TestPart1(t *testing.T) {
	tests := []struct {
		in  string
		exp int
	}{
		{"1122", 3},
		{"1111", 4},
		{"1234", 0},
		{"91212129", 9},
	}

	for _, tc := range tests {
		res := Part1(tc.in)
		assert.Equal(t, tc.exp, res)
	}
}

func TestSolvePart1(t *testing.T) {
	t.Log("Day1Part1:", Part1(""))
}

